9.4 Vector propio (w)

eigenvectors <- function(matriz, normalize = F){
    eigenvectors <- abs(eigen(matriz)$vectors)[,1]
    if(normalize==T){
        eigenvectors <- eigenvectors/sum(eigenvectors)
    }
    return(eigenvectors)
}


